    def test_new_filter(self):
        filter_obj = in3.eth.Filter(fromBlock=16095289)
        response = self.eth.new_filter(filter=filter_obj)
        self.assertEqual(response, "0x1")
        import time

        time.sleep(1)
        changes = self.eth.get_filter_logs(filter_id=response)
        print(changes)


    def test_new_block_filter(self):
        filter_obj = self.eth.new_block_filter()
        import time
        time.sleep(10)
        changes = self.eth.get_filter_changes(filter_id=filter_obj)
        for c in changes:
            block = self.eth.get_block_by_hash(c,False)
            self.assertIsNotNone(block.hash)
        self.assertTrue(len(changes)>0)
        self.assertEqual(filter_obj, "0x1")

